import React from 'react';
import axios from 'axios';
import './BookApp.css';

const backendURL = 'http://172.31.34.248:3001'; // Assuming your backend runs on port 3001

class BookApp extends React.Component {
  state = {
    formData: {
      title: '',
      author: '',
      description: ''
    },
    books: [],
    file: null
  };

  componentDidMount() {
    this.fetchBooks();
  }

  fetchBooks = async () => {
    try {
      const response = await axios.get(`${backendURL}/books`);
      this.setState({ books: response.data });
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  handleChange = (e) => {
    this.setState({
      formData: { ...this.state.formData, [e.target.name]: e.target.value }
    });
  };

  handleFileChange = (e) => {
    this.setState({ file: e.target.files[0] });
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('image', this.state.file);
    formData.append('title', this.state.formData.title);
    formData.append('author', this.state.formData.author);
    formData.append('description', this.state.formData.description);
    try {
      await axios.post(`${backendURL}/books`, formData);
      alert('Book added successfully');
      this.fetchBooks();
    } catch (error) {
      console.error('Error adding book:', error);
      alert('Error adding book');
    }
  };

  handleUpdate = async (id) => {
    const newDescription = prompt('Enter new description:');
    if (newDescription !== null) {
      try {
        await axios.put(`${backendURL}/books/${id}`, { description: newDescription });
        this.fetchBooks();
      } catch (error) {
        console.error('Error updating book:', error);
      }
    }
  };

  handleDelete = async (id) => {
    const confirmDelete = window.confirm('Are you sure you want to delete this book?');
    if (confirmDelete) {
      try {
        await axios.delete(`${backendURL}/books/${id}`);
        this.fetchBooks();
      } catch (error) {
        console.error('Error deleting book:', error);
      }
    }
  };

  render() {
    return (
      <div className="book-app">
        <div className="floating-container">
          <h2>Add Book</h2>
          <form onSubmit={this.handleSubmit}>
            <div className="form-group">
              <label>Title:</label>
              <input type="text" name="title" value={this.state.formData.title} onChange={this.handleChange} />
            </div>
            <div className="form-group">
              <label>Author:</label>
              <input type="text" name="author" value={this.state.formData.author} onChange={this.handleChange} />
            </div>
            <div className="form-group">
              <label>Description:</label>
              <textarea name="description" value={this.state.formData.description} onChange={this.handleChange} />
            </div>
            <div>
              <label>Image:</label>
              <input type="file" name="image" onChange={this.handleFileChange} />
            </div>
            <button type="submit">Add Book</button>
          </form>
        </div>

        <div className="floating-container">
          <h2>Book List</h2>
          <ul>
            {this.state.books.map((book) => (
              <li key={book.id}>
                <strong>Title:</strong> {book.title} | <strong>Author:</strong> {book.author} |{' '}
                <strong>Description:</strong> {book.description}{' '}
                <img src={`${backendURL}/uploads/${book.image}`} alt={book.title} />
                <button className="btn-update" onClick={() => this.handleUpdate(book.id)}>Update</button>{' '}
                <button className="btn-delete" onClick={() => this.handleDelete(book.id)}>Delete</button>
              </li>
            ))}
          </ul>
        </div>
      </div>
    );
  }
}

export default BookApp;
