import React from 'react';
import axios from 'axios';
import './BookApp.css';

class BookForm extends React.Component {
  state = {
    formData: {
      title: '',
      author: '',
      description: '',
      image: null, // Added image state
    }
  };

  handleChange = (e) => {
    this.setState({
      formData: { ...this.state.formData, [e.target.name]: e.target.value }
    });
  };

  handleImageChange = (e) => {
    this.setState({
      formData: { ...this.state.formData, image: e.target.files[0] }
    });
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append('title', this.state.formData.title);
      formData.append('author', this.state.formData.author);
      formData.append('description', this.state.formData.description);
      formData.append('image', this.state.formData.image);

      await axios.post('http://localhost:3001/books', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      alert('Book added successfully');
      this.setState({
        formData: {
          title: '',
          author: '',
          description: '',
          image: null
        }
      });
    } catch (error) {
      console.error('Error adding book:', error);
      alert('Error adding book');
    }
  };

  render() {
    return (
      <div className="floating-container">
        <h2>Add Book</h2>
        <form onSubmit={this.handleSubmit} encType="multipart/form-data">
          <div className="form-group">
            <label>Title:</label>
            <input type="text" name="title" value={this.state.formData.title} onChange={this.handleChange} />
          </div>
          <div className="form-group">
            <label>Author:</label>
            <input type="text" name="author" value={this.state.formData.author} onChange={this.handleChange} />
          </div>
          <div className="form-group">
            <label>Description:</label>
            <textarea name="description" value={this.state.formData.description} onChange={this.handleChange} />
          </div>
          <div className="form-group">
            <label>Image:</label>
            <input type="file" name="image" onChange={this.handleImageChange} accept="image/*" />
          </div>
          <button type="submit">Add Book</button>
        </form>
      </div>
    );
  }
}

const BookList = ({ books, handleUpdate, handleDelete }) => {
  return (
    <div className="floating-container">
      <h2>Book List</h2>
      <ul>
        {books.map((book) => (
          <li key={book.id}>
            <strong>Title:</strong> {book.title} | <strong>Author:</strong> {book.author} |{' '}
            <strong>Description:</strong> {book.description}{' '}
            <button className="btn-update" onClick={() => handleUpdate(book.id)}>Update</button>{' '}
            <button className="btn-delete" onClick={() => handleDelete(book.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

class BookApp extends React.Component {
  state = {
    formData: {
      title: '',
      author: '',
      description: '',
      image: null,
    },
    books: []
  };

  componentDidMount() {
    this.fetchBooks();
  }

  fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:3001/books');
      this.setState({ books: response.data });
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  handleChange = (e) => {
    this.setState({
      formData: { ...this.state.formData, [e.target.name]: e.target.value }
    });
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    // No need to handle form submit here since it's handled in BookForm component
  };

  handleUpdate = async (id) => {
    // Implementation for updating book description
  };

  handleDelete = async (id) => {
    // Implementation for deleting book
  };

  render() {
    return (
      <div className="book-app">
        <BookForm
          formData={this.state.formData}
          handleChange={this.handleChange}
          handleSubmit={this.handleSubmit}
        />
        <BookList
          books={this.state.books}
          handleUpdate={this.handleUpdate}
          handleDelete={this.handleDelete}
        />
      </div>
    );
  }
}

export default BookApp;
